import React,{useState, useEffect} from 'react';
import {getAnimal} from '../../api/api';
import {Container, Grid, Typography} from '@material-ui/core';
import styles from './animal-page.module.css';

const AnimalPage = (props) => {
    let [animal, setAnimal] = useState()
   
    let animalID = props.match.params.id;
    console.log('This is ID --->',props.match.params.id)
    useEffect(() => {
        const fetchAnimal = async () => {
            const fetchedAnimal = await getAnimal(animalID);
            setAnimal(fetchedAnimal.data.animal)
        }

        fetchAnimal()
        
    },[])
    console.log(animal)
   
    return (
        <Container maxWidth='lg'>
            <div className={styles.animalPage}>
            <Grid container direction='row' className={styles.mainGrid}>
            <h1>{
                animal ? 
                <div>
                   
                        {
                            animal.photos ? 
                            <Grid container direction='column'>
                                {
                                    animal.photos.map(photo => {
                                        return <img src={photo.medium} alt="animal-photo" className={styles.pagePhoto}/>
                                    })
                                }
                                
                            </Grid> : null
                        }
                    
                    </div>  : null
                }</h1>

                <Grid xs={8} className={styles.infoContainer}>
                    {
                        animal ?
                        <div>
                        <h1>{animal.name}</h1>
                       
                       <Grid direction='row'>
                        <Typography variant='body1'>
                           age: {animal.age}
                        </Typography>
                        <Typography variant='body1'>
                           size: {animal.size}
                        </Typography></Grid>
                        </div>  : null
                    }   
               
                   
                </Grid>

            </Grid>
        </div>
        </Container>
        
    )
}

export default AnimalPage;
