import {Client} from '@petfinder/petfinder-js';
let client = new Client({apiKey: "msoH5wpzdE229wIRzF4M4PUM61SsKRf8wXzLdGVvPeFLGUvRPb", secret: "TFFm4sKwgsT1YyJH8uMPoACe1Z8HkQcF4SHSxxsH"})

export const getAnimals = async (page) => {

    if(!page) {
        return await client.animal.search()
    } else {
        return await client.animal.search({page: page})
    }

  
       
}

export const getAnimal = async (id) => {

    try {
        return await client.animal.show(id)
    } catch (err) {
        console.log('Opps, some err', err)
    }

   
}

export const getOptionalAnimal = async (type, page, query) => {
    if(page) {
        try {
            const animals = await client.animal.search({type: type, page: page, ...query})
            return animals
        } catch (err) {
            console.log(err)
        }
    } else {
        try {
            const animals = await client.animal.search({type: type, ...query})
            return animals
        } catch (err) {
            console.log(err)
        }
    }
   
}

export const getOrganization = async (name) => {
    if(name) {
       const response = await client.organization.show(name);
       return response
    }
    
}

export const getTypes = async () => {
    const response = await client.getTypes.show()
    return response

}
