import React from 'react';
import {Container,Card, CardContent, Typography, Grid, CardMedia} from '@material-ui/core';
import {Link} from 'react-router-dom';
import PaginationBox from '../../components/pagination/pagination.component';
import Spinner from '../../components/spinner/spinner.component';
import noPhoto from '../../assets/images/no-photo.png'
import {getOptionalAnimal} from '../../api/api';
import styles from './dogs-page.module.css';

const DogsPage = () => {

    const [dogs, setDogs]  = useState([])
    useEffect(() => {
        const getAnimals = async () => {
            let animals = await getOptionalAnimal("Dog")
            setDogs(animals.data.animals) 
        }

        getAnimals()
    },[])

    const handlePagination = async (e, num) => {
        setDogs([])
        window.scrollTo(0,0)
        let nextAnimals = await getOptionalAnimal("Dog",num)
       
        setDogs(nextAnimals.data.animals)
    }
    console.log(dogs)

    return (
        <div className={styles.dogsPage}>
             <Container maxWidth='lg'>
                <Grid container direction='row' justify='center' className={styles.gridContainer}>
                    {
                        dogs[0] ?
                        dogs.map(animal => {
                            return  <Card className={styles.card} key={animal.id}>
                                    <CardContent>
                                    <CardMedia component='img' alt='animal image' heigh='140' image={animal.photos[0] ? animal.photos[0].medium : noPhoto}/> 
                                    <Typography color="textSecondary" gutterBottom>
                                            {animal.name}
                                        </Typography>
                                        <Link to={`/animal-page/${animal.id}`}>Find out more</Link>
                                    </CardContent>
                            </Card>
                        }) : <Spinner />
                    }
                </Grid>
                <PaginationBox pages={10} onChange={handlePagination} />
            </Container>
        </div> 
    )
}

export default DogsPage;
