import React, {useState} from 'react';
import {InputLabel,MenuItem, FormHelperText,FormControl,Select} from '@material-ui/core';

import "./sidemenu.styles.scss";

function SideMenu ({onChange}) {

    const [isCliked, setIsclicked] = useState(!false);
    
    
    
    
    const handleClick = (e) => {
     
        setIsclicked(!isCliked);
       const sideNav = document.querySelector('.side-nav');

        isCliked ? sideNav.style.width = '350px' :  sideNav.style.width = '0px'
      
    }

    

 

    return (
        <div className="side-menu">

        
           
        <div className='side-nav'>
        <a href='#' className="close-btn" onClick={handleClick}>&times;</a>

        
            <div>
            <FormControl >
            <InputLabel id="demo-simple-select-label">Age</InputLabel>
            <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={''}
            onChange={onChange}
            className='select-box'
            name='age'
        >
          <MenuItem value={''}>Any</MenuItem>
          <MenuItem value={10}>Ten</MenuItem>
          <MenuItem value={20}>Twenty</MenuItem>
          <MenuItem value={30}>Thirty</MenuItem>
        </Select>
      </FormControl>
            </div> 
            
        
        
            
         </div> 
        
        <div className='side-menu-button' onClick={handleClick}>
        <span className='side-menu-icon'>
            Just Click
            </span> 
            </div> 
   

      
       
          
        </div>
    )
}



export default SideMenu;
