import React from 'react';
import IconComponent from '../icon/icon.component';

import './icon-info.styles.scss';


const IconInfo = ({type, width, height}) => {

    return (
        <div className='small-icon-box'>
            <div className="icon-info-container">
                <IconComponent type={type} className='small-icon' style={ width ? {width: width} : null }/>
            </div>

            <span className='small-icon-span'>Carefully crafted components</span>  
        </div>
    )
}

export default IconInfo;
