import React,{useState, useEffect} from 'react';
import {getAnimal, getOrganization} from '../../api/api';
import {getLocation} from '../../api/geocode';
import {Container, Grid, Typography} from '@material-ui/core';
import styles from './animal-page.module.css';

const AnimalPage = (props) => {
    let [animal, setAnimal] = useState()
    let [id, setId] = useState()
    let [org, setOrg] = useState()
    let animalID = props.match.params.id;
    console.log('This is ID --->',props.match.params.id)
    useEffect(() => {
        const fetchAnimal = async () => {
            const fetchedAnimal = await getAnimal(animalID);
            setAnimal(fetchedAnimal.data.animal)
            setId(fetchedAnimal.data.animal.organization_id)
        }
        fetchAnimal()
        console.log(id, org) 
    },[])

    
    useEffect(() => {
        const getOrg = async () => {
                
            try {
              let resp = await getOrganization(id)
              setOrg(resp.data.organization)
              console.log(id, org)
            } catch (err) {
              console.log(err)
            }
             
    }
      getOrg()
      console.log('data===>',org)
    },[id])

    useEffect(() => {
        
        if(org) {
            const getAddres = async () => {
              let loc = await getLocation(org.address.address1)
              return loc
            }
           
        }
     
        console.log(getAddres)
    },[org])
    console.log(animal)
    console.log(id, org)
    // setOrganization(org.id)
    
   
    return (
        <Container maxWidth='lg'>
            <div className={styles.animalPage}>
            <Grid container direction='row' className={styles.mainGrid}>
            <h1>{
                animal ? 
                <div>
                   
                        {
                            animal.photos ? 
                            <Grid container direction='column'>
                                {
                                    animal.photos.map(photo => {
                                        return <img src={photo.medium} alt="animal-photo" className={styles.pagePhoto}/>
                                    })
                                }
                                
                            </Grid> : null
                        }
                    
                    </div>  : null
                }</h1>

                <Grid xs={8} className={styles.infoContainer}>
                    {
                        animal ?
                        <div>
                        <h1>{animal.name}</h1>
                       
                       <Grid container direction='row'>
                        <Typography variant='body1' className={styles.littleBox}>
                            {animal.age}
                        </Typography>
                        <Typography variant='body1' className={styles.littleBox}>
                            {animal.size}
                        </Typography>
                        <Typography variant='body1' className={styles.littleBox}>
                            {animal.gender}
                        </Typography>
                        </Grid>

                        <h1>About</h1>
                        <Typography variant='body1' className={styles.aboutItem}>
                           Spayed/neutered: {animal.attributes.spayed_neutered ? 'Yes' : 'No'}
                        </Typography>
                        <Typography variant='body1' className={styles.aboutItem}>
                           House trained: {animal.attributes.house_trained ? 'Yes' : 'No'}
                        </Typography>
                        <Typography variant='body1' className={styles.aboutItem}>
                          Special needs: {animal.attributes.special_needs ? 'Yes' : 'No'}
                        </Typography>
                        <Typography variant='body1' className={styles.aboutItem}>
                          Breed: {animal.breeds.primary}
                        </Typography>
                        </div>  : null
                    }   
               
                   
                </Grid>

            </Grid>
        </div>
        </Container>
        
    )
}

export default AnimalPage;
