import {Client} from '@petfinder/petfinder-js';
let client = new Client({apiKey: "msoH5wpzdE229wIRzF4M4PUM61SsKRf8wXzLdGVvPeFLGUvRPb", secret: "TFFm4sKwgsT1YyJH8uMPoACe1Z8HkQcF4SHSxxsH"})

export const getAnimals = async (page) => {

    if(!page) {
        return await client.animal.search()
    } else {
        return await client.animal.search({page: page})
    }

  
       
}

export const getAnimal = async (id) => {

    try {
        return await client.animal.show(id)
    } catch (err) {
        console.log('Opps, some err', err)
    }

   
}

export const getOptionalAnimal = async (type, page) => {
    if(page) {
        try {
            const animals = await client.animal.search({type: type, page: page})
            return animals
        } catch (err) {
            console.log(err)
        }
    } else {
        try {
            const animals = await client.animal.search({type: type})
            return animals
        } catch (err) {
            console.log(err)
        }
    }
   
}

export const filteredAnimals = async (query) => {
    const filteredAnimals = await client.animal.search({type: 'Dog',...query})
    return filteredAnimals
}
