import React,{useState, useEffect} from 'react';
import {getAnimal} from '../../api/api';
import {Container} from '@material-ui/core';
import styles from './animal-page.module.css';

const AnimalPage = (props) => {
    let [animal, setAnimal] = useState()
    // let animal = props.location.animalData;
    console.log('This is ID --->',props.match.params.id)
    useEffect(() => {
        const fetchAnimal = async () => {
            const fetchedAnimal = await getAnimal(48550353);
            setAnimal(fetchedAnimal.data.animal)
        }

        fetchAnimal()
        
    },[])
    console.log(animal)
    return (
        <Container maxWidth='lg'>
            <div className={styles.animalPage}>Animal page
            <h1>{
                animal ? 
                animal.name : null
                }</h1>
        </div>
        </Container>
        
    )
}

export default AnimalPage;
