import React, {useState} from 'react';
import {withRouter,Link} from "react-router-dom";
import {connect} from 'react-redux';

import ModalMessage from '../modal-message/modal-message.component';
import {ReactComponent as GymIcon} from '../../assets/gym.svg';


import "./sidemenu.styles.scss";

function SideMenu ({currentDictId,match, dict}) {

    const [isCliked, setIsclicked] = useState(!false);
    const [modalMessage, setModalMessage] = useState(false)

    console.log('This is match from sidemanu',match)
    
    
    const handleClick = (e) => {
       
        if(Object.keys(dict.Dictionary).length !== 0) {
            setIsclicked(!isCliked);
            const sideNav = document.querySelector('.side-nav');

            isCliked ? sideNav.style.width = '350px' :  sideNav.style.width = '0px'
        } else {
            e.preventDefault()
            // alert('You need to add at least one word!')
            setModalMessage(true)
        }
    }

    

 
 

    return (
        <div className="side-menu">

        
           
        <div className='side-nav'>
        <a href='#' className="close-btn" onClick={handleClick}>&times;</a>

        
            <div>
            <Link to={`${match.url}`} onClick={handleClick} className='side-menu-link' style={!isCliked ? {opacity: '1', transition: '1.3s'}: {opacity: '0'}}>Dictionary</Link>
            <Link to={`${match.url}/solotraining`} onClick={handleClick} className='side-menu-link' style={!isCliked ? {opacity: '1', transition: '1.3s'}: {opacity: '0'}}>Translation</Link> 
            <Link to={`${match.url}/build-word`} onClick={handleClick} className='side-menu-link' style={!isCliked ? {opacity: '1', transition: '1.3s'}: {opacity: '0'}}>Build Word</Link> 
            <Link to={`${match.url}/choose-word`} onClick={handleClick} className='side-menu-link' style={!isCliked ? {opacity: '1', transition: '1.3s'}: {opacity: '0'}}>Cards</Link> 
            </div> 
            
        
        
            
         </div> 
        
        <div className='side-menu-button' onClick={handleClick}>
        <span className='side-menu-icon'>
        <GymIcon className='gym-icon'/>
            </span> 
            </div> 
   

        {
            modalMessage ?
            <ModalMessage message='You need to add at least one word!' onClick={() => setModalMessage(false)}/> :
            null
        }
       
          
        </div>
    )
}


const mapStateToProps = state => ({
    currentDictId: state.currentDictId.currentDictId,
    dict: state.dictionary.currentDict
})

export default withRouter(connect(mapStateToProps)(SideMenu));
