import React,{useEffect, useState} from 'react';
import {Link} from 'react-router-dom';
import {getAnimals} from '../api/api';
import {Container,Card, CardContent, Typography, Grid, CardMedia} from '@material-ui/core';
import {Pagination} from '@material-ui/lab'
import Spinner from '../components/spinner/spinner.component';
import noPhoto from '../assets/images/no-photo.png'
import styles from './homepage.module.css';
const Homepage = () => {

    const [animals, setAnimals] = useState([])
    
    
    useEffect(() => {
        const data = async () => {
           let animal =  await getAnimals();
           setAnimals(animal.data.animals)
           console.log(animal)  
        }
      setAnimals(data)
     
    },[])

    const handlePagination = async (e, num) => {
        console.log(e.target)
        console.log(num)
        setAnimals([])
        window.scrollTo(0,0)
        let nextAnimals = await getAnimals(num)
       
        setAnimals(nextAnimals.data.animals)
    }
   
    if(animals[0]) {
        console.log(animals)
        
    }
    
    return (
        <Container maxWidth='lg'>
             <div className={styles.homepage}>
                 <Grid container direction='row' justify='center'>
                {
                    animals[0] ?
                        animals.map(animal => {
                            return  <Card className={styles.card} key={animal.id}>
                                    <CardContent>
                         <CardMedia component='img' alt='animal image' heigh='140' image={animal.photos[0] ? animal.photos[0].medium : noPhoto}/> 
                                    <Typography color="textSecondary" gutterBottom>
                                            {animal.name}
                                        </Typography>
                                        <Link to={`/animal-page/${animal.id}`}>Find out more</Link>
                                    </CardContent>
                            </Card>
                        }) : <Spinner /> }
                </Grid>
                
               <div className={styles.paginationBlock}>
                 <Pagination count={10} className={styles.pagination} onChange={handlePagination}/>
                   </div> 
               
            </div>
        </Container>
       
    )
}

export default Homepage;
