    let [animal, setAnimal] = useState()
    // let animal = props.location.animalData;
    console.log(getAnimal(48550353))
    useEffect(() => {
        setAnimal(props.location.animalData)
    },[])
    return (
        <div>Animal Page
            <h1>{
                animal ? 
                animal.name : null
                }</h1>
        </div>
    )
}

export default AnimalPage;
