import React,{useState, useEffect} from 'react';
import {getAnimal} from '../../api/api';
import {Container, Grid, Typography} from '@material-ui/core';
import styles from './animal-page.module.css';

const AnimalPage = (props) => {
    let [animal, setAnimal] = useState()
   
    let animalID = props.match.params.id;
    console.log('This is ID --->',props.match.params.id)
    useEffect(() => {
        const fetchAnimal = async () => {
            const fetchedAnimal = await getAnimal(animalID);
            setAnimal(fetchedAnimal.data.animal)
        }

        fetchAnimal()
        
    },[])
    console.log(animal)
   
    return (
        <Container maxWidth='lg'>
            <div className={styles.animalPage}>
            <Grid container direction='row'>
            <h1>{
                animal ? 
                <div>
                   
                        {
                            animal.photos ? 
                            <Grid container direction='column'>
                                 <img src={animal.photos[0].medium} alt="animal-photo" className={styles.pagePhoto}/>
                                 <img src={animal.photos[1].medium} alt="animal-photo" className={styles.pagePhoto}/>
                                 <img src={animal.photos[2].medium} alt="animal-photo" className={styles.pagePhoto}/>
                            </Grid> : null
                        }
                    
                    </div>  : null
                }</h1>

                <Grid xs={8} className={styles.infoContainer}>
                    {
                        animal ?
                        <div>
                        <h1>{animal.name}</h1>
                        <Typography variant='body1'>
                            {animal.description}
                        </Typography>
                        </div>  : null
                    }   
               
                   
                </Grid>

            </Grid>
        </div>
        </Container>
        
    )
}

export default AnimalPage;
