import React, {useState,useEffect} from 'react';
import {Container,Card, CardContent, Typography, Grid, CardMedia} from '@material-ui/core';
import AnimalCard from '../../components/animal-card/animal-card.component'
import SideMenu from '../../components/sidemenu/sidemenu.component'
import {Link} from 'react-router-dom';
import PaginationBox from '../../components/pagination/pagination.component';
import IconComponent from '../../components/icon/icon.component';
import Spinner from '../../components/spinner/spinner.component';
import noPhoto from '../../assets/images/no-photo.png'
import {getOptionalAnimal} from '../../api/api';
import styles from './dogs-page.module.css';

const DogsPage = () => {

    const [dogs, setDogs]  = useState([])
    const [pages, setPages] = useState(0)
    const [isClicked, setIsClicked] = useState(false)
    const [query, setQuery] = useState({
        gender: '',
        age: '',
        size: ''
    })
    useEffect(() => {
        const getAnimals = async () => {
            let animals = await getOptionalAnimal("Dog",1, query)
            setDogs(animals.data.animals)
            setPages(animals.data.pagination.total_pages) 
        }

        getAnimals()
    },[])

    const handlePagination = async (e, num) => {
        setDogs([])
        window.scrollTo(0,0)
        let nextAnimals = await getOptionalAnimal("Dog",num, query)
       
        setDogs(nextAnimals.data.animals)
    }

    const handleChange = (e) => {
        let name = e.target.name
        let value = e.target.value
        console.log(e.target)
        setQuery((prevState) => {
            return {
                ...prevState,
                [name]: value
            }
        })
    }

    const handleSubmit = async () => {
        console.log(query)
        setDogs([])
        window.scrollTo(0,0)
        let filter = await getOptionalAnimal("Dog", 1,query)
        setDogs(filter.data.animals)
        setPages(filter.data.pagination.total_pages)
        console.log(filter)
    }

   
    console.log(dogs)
    return (
        <div className={styles.dogsPage}>
            <SideMenu onChange={handleChange} query={query} onSubmit={handleSubmit}/>
             <Container maxWidth='lg'>
                <Grid container direction='row' justify='center' className={styles.gridContainer}>
                    {
                        dogs[0] ?
                        dogs.map(animal => {
                            return  <Card className={styles.card} key={animal.id}>
                                    <CardContent>
                                    <CardMedia component='img' alt='animal image' heigh='140' image={animal.photos[0] ? animal.photos[0].medium : noPhoto} className={styles.cardMedia}/> 
                                    <Typography color="textSecondary" gutterBottom>
                                            {animal.name}
                                        </Typography>
                                        <Grid container direction='row' justify='center'>
                                            <IconComponent type={animal.type}/>
                                            <IconComponent type={animal.gender}/>
                                            <IconComponent type={animal.contact.address.country}/>
                                           </Grid> 
                                        <Link to={`/animal-page/${animal.id}`}>Find out more</Link>
                                    </CardContent>
                            </Card>
                        }) : <Spinner />
                    }
                </Grid>
                <PaginationBox pages={pages} onChange={handlePagination} />
            </Container>
        </div> 
    )
}

export default DogsPage;
