import React from 'react';
import {Typography} from '@material-ui/core';
import styles from './org.module.css';


const OrgContainer = ({org}) => {

    return (
        <div className={styles.orgContainer}>
          
          <div className={styles.orgInfo}>
            <h1>Organization</h1> 
            <Typography variant='body1' className={styles.aboutItem}>
                  Name: <strong>{org.name}</strong> 
                  </Typography>  
                  <Typography variant='body1' className={styles.aboutItem}> Phone: { org.phone ? org.phone : "None" }  </Typography>
                  <Typography variant='body1' className={styles.aboutItem}> Email: {org.email ? org.email : "None"} </Typography> 
             </div>  

            
               
        

    </div>
    )
}

export default OrgContainer
