import React,{useEffect, useState} from 'react';
import {getAnimals} from '../api/api';
import {Container,Card, CardContent, Typography, Grid} from '@material-ui/core';
import Spinner from '../components/spinner/spinner.component';
import styles from './homepage.module.css';
const Homepage = () => {

    const [animals, setAnimals] = useState([])
    
    
    useEffect(() => {
        const data = async () => {
           let animal =  await getAnimals();
           setAnimals(animal.data.animals)
        }
      setAnimals(data)
     
    },[])

  
   
    if(animals[0]) {
        console.log(animals[0].id)
        animals.map(function(item){
            console.log(item)
        })
    }
    
    return (
        <Container maxWidth='lg'>
             <div className={styles.homepage}>
                 <Grid container direction='row' justify='center'>
                {
                    animals[0] ?
                        animals.map(animal => {
                            return 
                             <Card>
                                    <CardContent>
                                    <Typography color="textSecondary" gutterBottom>
                                            {animal.name}
                                        </Typography>
                                    </CardContent>
                            </Card>
                        }) : <Spinner /> }
                </Grid>
            </div>
        </Container>
       
    )
}

export default Homepage;
