import React,{useState, useEffect} from 'react';
import {getAnimal} from '../../api/api';
import {Container, Grid} from '@material-ui/core';
import styles from './animal-page.module.css';

const AnimalPage = (props) => {
    let [animal, setAnimal] = useState()
   
    let animalID = props.match.params.id;
    console.log('This is ID --->',props.match.params.id)
    useEffect(() => {
        const fetchAnimal = async () => {
            const fetchedAnimal = await getAnimal(animalID);
            setAnimal(fetchedAnimal.data.animal)
        }

        fetchAnimal()
        
    },[])
    console.log(animal)
    return (
        <Container maxWidth='lg'>
            <div className={styles.animalPage}>Animal page
            <h1>{
                animal ? 
                <div>
                   
                        {
                            animal.photos ? 
                            <Grid direction='column'>
                                 <img src={animal.photos[0].medium} alt="animal-photo"/>
                                 <img src={animal.photos[1].medium} alt="animal-photo"/>
                                 <img src={animal.photos[2].medium} alt="animal-photo"/>
                            </Grid> : null
                        }
                       
                    <h1>{animal.name}</h1>
                    <img src={animal.photos[0].large} alt="animal-photo"/>
                    </div>  : null
                }</h1>
        </div>
        </Container>
        
    )
}

export default AnimalPage;
