import React,{useState, useEffect} from 'react';
import {getAnimal, getOrganization, getTypes} from '../../api/api';
import {getLocation} from '../../api/geocode';
import {Container, Grid, Typography} from '@material-ui/core';
import Modal from '../../components/modal/modal.component';
import NoImage from '../../assets/images/no-image.jpg'
import OrgContainer from '../../components/org/org.component';
import GoogleMap from '../../components/google-map/google-map.component';
import styles from './animal-page.module.css';

const AnimalPage = (props) => {
    let [animal, setAnimal] = useState()
    let [id, setId] = useState()
    let [org, setOrg] = useState()
    let [location, setLocation] = useState({})
    let [modal, setModal] = useState({
        active: false,
        src: ''
    })
    let animalID = props.match.params.id;
    console.log('This is ID --->',props.match.params.id)
    useEffect(() => {
        const fetchAnimal = async () => {
            const fetchedAnimal = await getAnimal(animalID);
            setAnimal(fetchedAnimal.data.animal)
            setId(fetchedAnimal.data.animal.organization_id)
        }
        fetchAnimal()
        console.log(id, org) 
    },[])

    
    useEffect(() => {
        const getOrg = async () => {
                
            try {
              let resp = await getOrganization(id)
              setOrg(resp.data.organization)
              console.log(id, org)
            } catch (err) {
              console.log(err)
            }
             
    }
      getOrg()
      console.log('data===>',org)
    },[id])

    useEffect(() => {
        
            if(org) {
                const getAddres = async () => {
                   try {
                    const loc = await getLocation(org.address.address1)
                    setLocation(loc.results[0].geometry.location)
                     } catch(err) {
                         console.log(err)
                     }
                  
                }
                
                getAddres()
            }
        
           
        
      
     
       
    },[org])
    console.log(animal)
    // console.log(id, org)
    // console.log(location)
    // setOrganization(org.id)
    const handlePhoto = (e) => {
        let imgId = e.target.id
        // let imgUrl = animal.photos[0].full
       
     
        setModal({ active: true, src: animal.photos[imgId].full})
        
    }

    const handleClose = () => {
        setModal(() =>{
            return {
                active: false,
                src: ''
            }
        })
    }
   
    return (
        <Container maxWidth='lg'>
            <Modal active={modal.active} src={modal.src} onClose={handleClose} />
            <div className={styles.animalPage}>
            <Grid container direction='row'  className={styles.mainGrid}>
            <h1>{
                animal ? 
                <div>
                   
                        {
                            animal.photos[0] ? 
                            <Grid container direction='column'> 
                                {
                                    animal.photos.map((photo, index) => {
                                        return <img src={photo.medium} alt="animal-photo" className={styles.pagePhoto} onClick={handlePhoto} id={index}/>
                                    })
                                }
                                
                            </Grid> :  <img src={NoImage} alt="no-image" className={styles.noPagePhoto}/>
                        }
                    
                    </div>  : null
                }</h1>

                <Grid lg={8} sm={12} className={styles.infoContainer}>
                    {
                        animal ?
                        <div>
                        <h1>{animal.name}</h1>
                       
                       <Grid lg={8} sm={12} container direction='row'>
                       <Typography variant='body1' className={styles.littleBox}>
                         {animal.gender}
                        </Typography>
                        <Typography variant='body1' className={styles.littleBox}>
                             {animal.age}  
                        </Typography>
                        <Typography variant='body1' className={styles.littleBox}>
                           {animal.size}     
                        </Typography>
                        
                        <Typography variant='body1' className={styles.littleBox}>
                         {animal.species}
                        </Typography>
                        </Grid>

                        <h1>About</h1>
                        <Typography variant='body1' className={styles.aboutItem}>
                           Spayed/neutered:  <strong>{animal.attributes.spayed_neutered ? 'Yes' : 'No'}</strong>  
                        </Typography>
                        <Typography variant='body1' className={styles.aboutItem}>
                           House trained:  <strong>{animal.attributes.house_trained ? 'Yes' : 'No'}</strong>  
                        </Typography>
                        <Typography variant='body1' className={styles.aboutItem}>
                          Special needs:  <strong>{animal.attributes.special_needs ? 'Yes' : 'No'}</strong>  
                        </Typography>
                        <Typography variant='body1' className={styles.aboutItem}>
                          Breed:  <strong>{animal.breeds.primary}</strong>  
                        </Typography>
                        </div>  : null
                    }  

                        {
                            org ? 
                            <OrgContainer org={org}/>
                            : null
                        }
                       
                     {
                         location.lat ?
                         <GoogleMap location={location} /> :
                         null
                     }   
                 
                    
                   
                </Grid>

            </Grid>
        </div>
        </Container>
        
    )
}

export default AnimalPage;
