import React, {useState} from 'react'
import GoogleMapReact from 'google-map-react';
import {ReactComponent as PinIcon} from '../../assets/icons/pin.svg'
import styles from './google-map.module.css'

const GoogleMap = ({location}) => {

     const [defLocation,setDefLocation] = useState({
         lat: location.lat,
         lng: location.lng
     }) 

     console.log(location)


    const simpleText = () => (<div className={styles.pinBox}><PinIcon className={styles.pinIcon}/></div>   )

    return (
        <div className={styles.mapContainer}>
            {
                location ?
                <GoogleMapReact 
                bootstrapURLKeys={{key: 'AIzaSyBK4jEdhF1BVg5IolsDUQSXZsUWVEzKzOs'}}
                defaultCenter={defLocation}
                defaultZoom={11}
            >
               <PinIcon lat={location.lat} lng={location.lng} className={styles.pinIcon} /> 
            </GoogleMapReact> : null
            }
            
        </div>
    )
}

export default GoogleMap;
